var button = document.getElementById("notifo");
button.addEventListener('click', function(e) {
	Notification.requestPermission().then(function(result) {
		if(result === 'granted') {
			randomNotification();
		}
	});
});

function randomNotification() {
	var deskripsi = ['Lahir : 18 Maret 2000', ' Usia : 19 Tahun', 'Tinggal di : Matangkuli, Aceh Utara', 'Kuliah : Universitas Malikussaleh']; 
	var notifTitle = 'Saiful Riza';
	var notifBody = deskripsi[Math.floor(Math.random() * deskripsi.length)];
	var notifImg = './assets/img/icon-72x72.png';
	var options = {
		body: notifBody,
		icon: notifImg,
	}
	var notif = new Notification(notifTitle, options);
	setTimeout(randomNotification, 30000);
};